datastream_buffer = ''


def part1(no_of_distinct_chars: int):
    chars = list(datastream_buffer)
    current_marker = ''
    current_index = 0
    
    while len(current_marker) < no_of_distinct_chars:
        char = chars[current_index]
        if current_marker.__contains__(char):
            # Found a repeat, need to delete up until repeating char
            index_of_repeat = 0
            repeat_found = False
            while not repeat_found:
                char2 = current_marker[index_of_repeat]
                if char == char2:
                    repeat_found = True
                    current_marker = current_marker[index_of_repeat + 1:]
                    current_marker = current_marker + char
                else:
                    index_of_repeat += 1
        else:
            # No repeat, continue looking
            current_marker = current_marker + char
        
        current_index += 1
    
    print(current_index)


